import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const languages = [
  { code: "en", name: "English" }, { code: "hi", name: "Hindi" },
  { code: "fr", name: "French" }, { code: "es", name: "Spanish" },
  { code: "de", name: "German" }, { code: "ta", name: "Tamil" },
  { code: "zh", name: "Chinese" }, { code: "ar", name: "Arabic" },
  { code: "ru", name: "Russian" }, { code: "ja", name: "Japanese" },
  { code: "it", name: "Italian" }, { code: "pt", name: "Portuguese" },
  { code: "pl", name: "Polish" }, { code: "tr", name: "Turkish" },
  { code: "nl", name: "Dutch" }, { code: "sv", name: "Swedish" },
  { code: "ko", name: "Korean" }, { code: "fa", name: "Persian" },
  { code: "fi", name: "Finnish" }, { code: "he", name: "Hebrew" },
  { code: "uk", name: "Ukrainian" }, { code: "vi", name: "Vietnamese" },
  { code: "cs", name: "Czech" }, { code: "ro", name: "Romanian" }
];

const languageIcons = {
  en: "ðŸ‡¬ðŸ‡§", hi: "ðŸ‡®ðŸ‡³", fr: "ðŸ‡«ðŸ‡·", es: "ðŸ‡ªðŸ‡¸", de: "ðŸ‡©ðŸ‡ª", ta: "ðŸ‡®ðŸ‡³",
  zh: "ðŸ‡¨ðŸ‡³", ar: "ðŸ‡¸ðŸ‡¦", ru: "ðŸ‡·ðŸ‡º", ja: "ðŸ‡¯ðŸ‡µ", it: "ðŸ‡®ðŸ‡¹", pt: "ðŸ‡µðŸ‡¹",
  pl: "ðŸ‡µðŸ‡±", tr: "ðŸ‡¹ðŸ‡·", nl: "ðŸ‡³ðŸ‡±", sv: "ðŸ‡¸ðŸ‡ª", ko: "ðŸ‡°ðŸ‡·", fa: "ðŸ‡®ðŸ‡·",
  fi: "ðŸ‡«ðŸ‡®", he: "ðŸ‡®ðŸ‡±", uk: "ðŸ‡ºðŸ‡¦", vi: "ðŸ‡»ðŸ‡³", cs: "ðŸ‡¨ðŸ‡¿", ro: "ðŸ‡·ðŸ‡´"
};

function App() {
  const [text, setText] = useState("");
  const [targetLangs, setTargetLangs] = useState([]);
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(false);

  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    if (e.target.checked) {
      if (targetLangs.length < 5) {
        setTargetLangs([...targetLangs, value]);
      } else {
        alert("You can select up to 5 languages only.");
      }
    } else {
      setTargetLangs(targetLangs.filter((lang) => lang !== value));
    }
  };

  const handleTranslate = async () => {
    if (!text || targetLangs.length === 0) return;
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/translate", {
        text,
        target_langs: targetLangs,
      });
      setTranslations(response.data.translations || {});
    } catch (error) {
      alert("Translation failed");
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied!");
  };

  const handleSpeak = (text, langCode) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = langCode;
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="container">
      <h1>Multi-Language Translator</h1>
      <textarea
        placeholder="Enter text here..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        rows="4"
      />

      <div className="selectors">
        <label>Select up to 5 target languages:</label>
        <div className="checkbox-list">
          {languages.map((lang) => (
            <label key={lang.code}>
              <input
                type="checkbox"
                value={lang.code}
                checked={targetLangs.includes(lang.code)}
                onChange={handleCheckboxChange}
              />
              {languageIcons[lang.code]} {lang.name}
            </label>
          ))}
        </div>
      </div>

      <button onClick={handleTranslate} disabled={loading}>
        {loading ? "Translating..." : "Translate"}
      </button>

      <div className="results">
        {Object.entries(translations).map(([lang, result]) => (
          <div className="card" key={lang}>
            <h3>{languageIcons[lang]} {languages.find(l => l.code === lang)?.name}</h3>
            <p>{result}</p>
            <div className="card-buttons">
              <button onClick={() => handleCopy(result)}>ðŸ“‹ Copy</button>
              <button onClick={() => handleSpeak(result, lang)}>ðŸ”Š Read</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
