// frontend_react/src/App.js
import React, { useState, useRef } from "react";
import axios from "axios";
import "./App.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import TextField from "@mui/material/TextField";

const languages = [
  { code: "ar", name: "ÿßŸÑÿπÿ±ÿ®Ÿäÿ© (Arabic)" },
  { code: "zh", name: "‰∏≠Êñá (Chinese)" },
  { code: "cs", name: "ƒåe≈°tina (Czech)" },
  { code: "nl", name: "Nederlands (Dutch)" },
  { code: "en", name: "English (English)" },
  { code: "fi", name: "Suomi (Finnish)" },
  { code: "fr", name: "Fran√ßais (French)" },
  { code: "de", name: "Deutsch (German)" },
  { code: "he", name: "◊¢◊ë◊®◊ô◊™ (Hebrew)" },
  { code: "hi", name: "‡§π‡§ø‡§®‡•ç‡§¶‡•Ä (Hindi)" },
  { code: "it", name: "Italiano (Italian)" },
  { code: "ja", name: "Êó•Êú¨Ë™û (Japanese)" },
  { code: "ko", name: "ÌïúÍµ≠Ïñ¥ (Korean)" },
  { code: "fa", name: "ŸÅÿßÿ±ÿ≥€å (Persian)" },
  { code: "pl", name: "Polski (Polish)" },
  { code: "pt", name: "Portugu√™s (Portuguese)" },
  { code: "ro", name: "Rom√¢nƒÉ (Romanian)" },
  { code: "ru", name: "–†—É—Å—Å–∫–∏–π (Russian)" },
  { code: "sv", name: "Svenska (Swedish)" },
  { code: "ta", name: "‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç (Tamil)" },
  { code: "tr", name: "T√ºrk√ße (Turkish)" },
  { code: "uk", name: "–£–∫—Ä–∞—ó–Ω—Å—å–∫–∞ (Ukrainian)" },
  { code: "vi", name: "Ti·∫øng Vi·ªát (Vietnamese)" },
  { code: "es", name: "Espa√±ol (Spanish)" }
];

const languageIcons = {
  en: "üá¨üáß", hi: "üáÆüá≥", fr: "üá´üá∑", es: "üá™üá∏", de: "üá©üá™", ta: "üáÆüá≥", zh: "üá®üá≥", ar: "üá∏üá¶",
  ru: "üá∑üá∫", ja: "üáØüáµ", it: "üáÆüáπ", pt: "üáµüáπ", pl: "üáµüá±", tr: "üáπüá∑", nl: "üá≥üá±", sv: "üá∏üá™",
  ko: "üá∞üá∑", fa: "üáÆüá∑", fi: "üá´üáÆ", he: "üáÆüá±", uk: "üá∫üá¶", vi: "üáªüá≥", cs: "üá®üáø", ro: "üá∑üá¥"
};

export default function App() {
  const [text, setText] = useState("");
  const [targetLangs, setTargetLangs] = useState([]);
  const [translations, setTranslations] = useState({});
  const [detectedLang, setDetectedLang] = useState("");
  const [recording, setRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const backendURL = "http://127.0.0.1:8000"; // Adjust for your backend

  // Handle language selection
  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    if (e.target.checked) {
      if (targetLangs.length < 5) {
        setTargetLangs([...targetLangs, value]);
      } else {
        toast.warning("You can select a maximum of 5 target languages.");
        e.target.checked = false;
      }
    } else {
      setTargetLangs(targetLangs.filter((lang) => lang !== value));
    }
  };

  // Text-based translation
  const handleTranslate = async (overrideText = null) => {
    const textToTranslate = overrideText ?? text;
    if (!textToTranslate || targetLangs.length === 0) {
      toast.warning("Enter text and select at least one target language.");
      return;
    }
    try {
      const res = await axios.post(`${backendURL}/translate`, {
        text: textToTranslate,
        target_langs: targetLangs
      });
      setTranslations(res.data.translations || {});
      setDetectedLang(res.data.detectedLang || "");
    } catch (err) {
      toast.error("Text translation failed.");
      console.error(err);
    }
  };

  // Start recording for voice input
  const startRecording = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      toast.error("Microphone not supported");
      return;
    }
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioChunksRef.current = [];
      const mr = new MediaRecorder(stream);
      mediaRecorderRef.current = mr;

      mr.ondataavailable = (e) => {
        if (e.data && e.data.size > 0) audioChunksRef.current.push(e.data);
      };

      mr.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        const form = new FormData();
        form.append("file", audioBlob, "voice.webm");
        targetLangs.forEach(t => form.append("target_langs", t));
        try {
          toast.info("Processing your voice input...");
          const resp = await axios.post(`${backendURL}/voice-translate`, form, {
            headers: { "Content-Type": "multipart/form-data" }
          });
          if (resp.data.error) {
            toast.error("Server error: " + resp.data.error);
            return;
          }
          setText(resp.data.originalText || "");
          setTranslations(resp.data.translations || {});
          setDetectedLang(resp.data.detectedLang || "");
        } catch (err) {
          toast.error("Voice translation failed");
          console.error(err);
        }
      };

      mr.start();
      setRecording(true);
      toast.info("Recording... speak now.");
    } catch (err) {
      toast.error("Cannot access microphone: " + err.message);
    }
  };

  // Stop recording
  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  const handleCopy = (t) => {
    navigator.clipboard.writeText(t);
    toast.success("Copied!");
  };

  const handleSpeak = (txt, langCode) => {
    const u = new SpeechSynthesisUtterance(txt);
    u.lang = langCode || "en-US";
    window.speechSynthesis.speak(u);
  };

  return (
    <div className="container">
      <h1>üåç Multi-Language Voice & Text Translator</h1>

      <TextField
        label="Type text or use voice"
        multiline
        minRows={4}
        fullWidth
        value={text}
        onChange={(e) => setText(e.target.value)}
      />

      {detectedLang && <div>Detected language: {detectedLang}</div>}

      <div style={{ marginTop: 12 }}>
        <button onClick={() => handleTranslate()}>Translate Text</button>
        {!recording ? (
          <button onClick={startRecording} style={{ marginLeft: 12 }}>üé§ Start Voice</button>
        ) : (
          <button onClick={stopRecording} style={{ marginLeft: 12 }}>‚èπ Stop Voice</button>
        )}
      </div>

      <div className="checkbox-list" style={{ marginTop: 12 }}>
        {languages.map((lang) => (
          <label key={lang.code} style={{ display: "block" }}>
            <input
              type="checkbox"
              value={lang.code}
              onChange={handleCheckboxChange}
              checked={targetLangs.includes(lang.code)}
            />{" "}
            {languageIcons[lang.code]} {lang.name}
          </label>
        ))}
      </div>

      <div style={{ marginTop: 18 }}>
        <h3>Translations</h3>
        {Object.entries(translations).map(([lang, txt]) => (
          <div key={lang} style={{ border: "1px solid #ddd", padding: 8, marginBottom: 8 }}>
            <h4>{languageIcons[lang]} {languages.find(l => l.code === lang)?.name}</h4>
            <p>{txt}</p>
            <button onClick={() => handleCopy(txt)}>Copy</button>
            <button onClick={() => handleSpeak(txt, lang)}>Speak</button>
          </div>
        ))}
      </div>

      <ToastContainer position="bottom-right" autoClose={3000} />
    </div>
  );
}
