import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const languages = [
  { code: "en", name: "English" },
  { code: "hi", name: "Hindi" },
  { code: "fr", name: "French" },
  { code: "es", name: "Spanish" },
  { code: "de", name: "German" },
  { code: "ta", name: "Tamil" },
  { code: "zh", name: "Chinese" },
  { code: "ar", name: "Arabic" }
];

function App() {
  const [text, setText] = useState("");
  const [sourceLang, setSourceLang] = useState("en");
  const [targetLangs, setTargetLangs] = useState([]);
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(false);

  const handleSelectChange = (e) => {
    const selected = Array.from(e.target.selectedOptions).map(o => o.value);
    if (selected.length <= 5) setTargetLangs(selected);
  };

  const handleTranslate = async () => {
    if (!text || targetLangs.length === 0) return;
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/translate", {
        text,
        source_lang: sourceLang,
        target_langs: targetLangs
      });
      setTranslations(response.data.translations);
    } catch (error) {
      alert("Translation failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Multi-Output Offline Translator</h1>
      <textarea
        placeholder="Enter text to translate..."
        rows="4"
        cols="60"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />

      <div style={{ marginTop: "10px" }}>
        <label>Source Language: </label>
        <select value={sourceLang} onChange={(e) => setSourceLang(e.target.value)}>
          {languages.map(lang => (
            <option key={lang.code} value={lang.code}>{lang.name}</option>
          ))}
        </select>
      </div>

      <div style={{ marginTop: "10px" }}>
        <label>Target Languages (up to 5): </label>
        <select multiple value={targetLangs} onChange={handleSelectChange} size="5">
          {languages.map(lang => (
            <option key={lang.code} value={lang.code}>{lang.name}</option>
          ))}
        </select>
      </div>

      <button onClick={handleTranslate} disabled={loading} style={{ marginTop: "15px" }}>
        {loading ? "Translating..." : "Translate"}
      </button>

      <div className="results">
        {Object.entries(translations).map(([lang, result]) => (
          <div key={lang} className="translation-card">
            <strong>{languages.find(l => l.code === lang)?.name || lang}:</strong>
            <p>{result}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
