import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const languages = [
  { code: "en", name: "English" }, { code: "hi", name: "Hindi" },
  { code: "fr", name: "French" }, { code: "es", name: "Spanish" },
  { code: "de", name: "German" }, { code: "ta", name: "Tamil" },
  { code: "zh", name: "Chinese" }, { code: "ar", name: "Arabic" },
  { code: "ru", name: "Russian" }, { code: "ja", name: "Japanese" },
  { code: "it", name: "Italian" }, { code: "pt", name: "Portuguese" },
  { code: "pl", name: "Polish" }, { code: "tr", name: "Turkish" },
  { code: "nl", name: "Dutch" }, { code: "sv", name: "Swedish" },
  { code: "ko", name: "Korean" }, { code: "fa", name: "Persian" },
  { code: "fi", name: "Finnish" }, { code: "he", name: "Hebrew" },
  { code: "uk", name: "Ukrainian" }, { code: "vi", name: "Vietnamese" },
  { code: "cs", name: "Czech" }, { code: "ro", name: "Romanian" }
];

function App() {
  const [text, setText] = useState("");
  const [targetLangs, setTargetLangs] = useState([]);
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(false);

  const handleSelectChange = (e) => {
    const selected = Array.from(e.target.selectedOptions).map(o => o.value);
    if (selected.length <= 5) setTargetLangs(selected);
  };

  const handleTranslate = async () => {
    if (!text || targetLangs.length === 0) return;
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/translate", {
        text,
        target_langs: targetLangs
      });
      setTranslations(response.data.translations);
    } catch (error) {
      alert("Translation failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Multi-Language Translator</h1>
      <textarea
        placeholder="Type something..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        rows="5"
      />
      <div className="selectors">
        <label>Target Languages (max 5):</label>
        <select multiple value={targetLangs} onChange={handleSelectChange} size="5">
          {languages.map(lang => (
            <option key={lang.code} value={lang.code}>{lang.name}</option>
          ))}
        </select>
      </div>
      <button onClick={handleTranslate} disabled={loading}>
        {loading ? "Translating..." : "Translate"}
      </button>

      <div className="results">
        {Object.entries(translations).map(([lang, translation]) => (
          <div className="card" key={lang}>
            <h3>{languages.find(l => l.code === lang)?.name}</h3>
            <p>{translation}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
