import React, { useState, useEffect } from "react";
import axios from "axios";
import init, { LanguageDetectorBuilder } from "@bluzzi/lingua";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import TextField from "@mui/material/TextField";
import "./App.css";

const languages = [
  { code: "ar", name: "ÿßŸÑÿπÿ±ÿ®Ÿäÿ© (Arabic)" },
  { code: "zh", name: "‰∏≠Êñá (Chinese)" },
  { code: "cs", name: "ƒåe≈°tina (Czech)" },
  { code: "nl", name: "Nederlands (Dutch)" },
  { code: "en", name: "English (English)" },
  { code: "fi", name: "Suomi (Finnish)" },
  { code: "fr", name: "Fran√ßais (French)" },
  { code: "de", name: "Deutsch (German)" },
  { code: "he", name: "◊¢◊ë◊®◊ô◊™ (Hebrew)" },
  { code: "hi", name: "‡§π‡§ø‡§®‡•ç‡§¶‡•Ä (Hindi)" },
  { code: "it", name: "Italiano (Italian)" },
  { code: "ja", name: "Êó•Êú¨Ë™û (Japanese)" },
  { code: "ko", name: "ÌïúÍµ≠Ïñ¥ (Korean)" },
  { code: "fa", name: "ŸÅÿßÿ±ÿ≥€å (Persian)" },
  { code: "pl", name: "Polski (Polish)" },
  { code: "pt", name: "Portugu√™s (Portuguese)" },
  { code: "ro", name: "Rom√¢nƒÉ (Romanian)" },
  { code: "ru", name: "–†—É—Å—Å–∫–∏–π (Russian)" },
  { code: "sv", name: "Svenska (Swedish)" },
  { code: "ta", name: "‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç (Tamil)" },
  { code: "tr", name: "T√ºrk√ße (Turkish)" },
  { code: "uk", name: "–£–∫—Ä–∞—ó–Ω—Å—å–∫–∞ (Ukrainian)" },
  { code: "vi", name: "Ti·∫øng Vi·ªát (Vietnamese)" },
  { code: "es", name: "Espa√±ol (Spanish)" }
];

const languageIcons = {
  en: "üá¨üáß", hi: "üáÆüá≥", fr: "üá´üá∑", es: "üá™üá∏", de: "üá©üá™", ta: "üáÆüá≥", zh: "üá®üá≥", ar: "üá∏üá¶",
  ru: "üá∑üá∫", ja: "üáØüáµ", it: "üáÆüáπ", pt: "üáµüáπ", pl: "üáµüá±", tr: "üáπüá∑", nl: "üá≥üá±", sv: "üá∏üá™",
  ko: "üá∞üá∑", fa: "üáÆüá∑", fi: "üá´üáÆ", he: "üáÆüá±", uk: "üá∫üá¶", vi: "üáªüá≥", cs: "üá®üáø", ro: "üá∑üá¥"
};

function App() {
  const [text, setText] = useState("");
  const [targetLangs, setTargetLangs] = useState([]);
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(false);
  const [detectedLang, setDetectedLang] = useState("");
  const [detector, setDetector] = useState(null);

  // Load Lingua WASM
  useEffect(() => {
    (async () => {
      await init();
      const detectorInstance = LanguageDetectorBuilder
        .fromAllLanguages()
        .withPreloadedDetectableLanguages()
        .build();
      setDetector(detectorInstance);
    })();
  }, []);

  const handleTextChange = (e) => {
    const input = e.target.value;
    setText(input);

    if (detector && input.length > 2) {
      const best = detector.computeLanguageConfidenceValues(input)
        .sort((a, b) => b.confidence - a.confidence)[0];

      if (best && best.language) {
        const iso = best.language.toLowerCase();
        setDetectedLang(iso);
      } else {
        setDetectedLang("");
      }
    }
  };

  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    if (e.target.checked) {
      if (targetLangs.length < 5) {
        setTargetLangs([...targetLangs, value]);
      } else {
        toast.warning("You can select up to 5 languages only.");
        e.target.checked = false;
      }
    } else {
      setTargetLangs(targetLangs.filter((lang) => lang !== value));
    }
  };

  const handleTranslate = async () => {
    if (!text || targetLangs.length === 0) return;
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/translate", {
        text,
        target_langs: targetLangs,
        source_lang: detectedLang || "en"
      });
      setTranslations(response.data.translations || {});
    } catch (error) {
      toast.error("Translation failed.");
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    toast.success("Copied to clipboard!");
  };

  const handleSpeak = (text, langCode) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = langCode;
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="container">
      <h1>üåê Multi-Language Translator</h1>

      <TextField
        label={detector ? "Enter text..." : "Loading language detector..."}
        multiline
        rows={4}
        fullWidth
        variant="outlined"
        value={text}
        onChange={handleTextChange}
        disabled={!detector}
      />

      {detectedLang && (
        <div className="detected">
          üîç Detected Language: {languages.find(l => l.code === detectedLang)?.name || "Unknown"}
        </div>
      )}

      <div className="checkbox-list">
        {languages.map((lang) => (
          <label key={lang.code}>
            <input
              type="checkbox"
              value={lang.code}
              onChange={handleCheckboxChange}
              checked={targetLangs.includes(lang.code)}
            />
            {languageIcons[lang.code]} {lang.name}
          </label>
        ))}
      </div>

      <button onClick={handleTranslate} disabled={loading}>
        {loading ? "Translating..." : "Translate"}
      </button>

      <div className="results">
        {Object.entries(translations).map(([lang, result]) => (
          <div className="card" key={lang}>
            <h3>{languageIcons[lang]} {languages.find(l => l.code === lang)?.name}</h3>
            <p>{result}</p>
            <div className="card-buttons">
              <button onClick={() => handleCopy(result)}>üìã Copy</button>
              <button onClick={() => handleSpeak(result, lang)}>üîä Read</button>
            </div>
          </div>
        ))}
      </div>

      <ToastContainer position="bottom-right" autoClose={3000} />
    </div>
  );
}

export default App;
