import React, { useState } from "react";
import axios from "axios";
import { franc } from "franc-min";
import {iso6393} from "iso-639-3";
import "./App.css";

const languages = [
  { code: "en", name: "English" }, { code: "hi", name: "Hindi" },
  { code: "fr", name: "French" }, { code: "es", name: "Spanish" },
  { code: "de", name: "German" }, { code: "ta", name: "‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç" },
  { code: "zh", name: "Chinese" }, { code: "ar", name: "ÿπÿ±ÿ®Ÿä" },
  { code: "ru", name: "Russian" }, { code: "ja", name: "Japanese" },
  { code: "it", name: "Italian" }, { code: "pt", name: "Portuguese" },
  { code: "pl", name: "Polish" }, { code: "tr", name: "Turkish" },
  { code: "nl", name: "Dutch" }, { code: "sv", name: "Swedish" },
  { code: "ko", name: "Korean" }, { code: "fa", name: "Persian" },
  { code: "fi", name: "Finnish" }, { code: "he", name: "Hebrew" },
  { code: "uk", name: "Ukrainian" }, { code: "vi", name: "Vietnamese" },
  { code: "cs", name: "Czech" }, { code: "ro", name: "Romanian" }
];

const languageIcons = {
  en: "üá¨üáß", hi: "üáÆüá≥", fr: "üá´üá∑", es: "üá™üá∏", de: "üá©üá™", ta: "üáÆüá≥", zh: "üá®üá≥", ar: "üá∏üá¶",
  ru: "üá∑üá∫", ja: "üáØüáµ", it: "üáÆüáπ", pt: "üáµüáπ", pl: "üáµüá±", tr: "üáπüá∑", nl: "üá≥üá±", sv: "üá∏üá™",
  ko: "üá∞üá∑", fa: "üáÆüá∑", fi: "üá´üáÆ", he: "üáÆüá±", uk: "üá∫üá¶", vi: "üáªüá≥", cs: "üá®üáø", ro: "üá∑üá¥"
};

function App() {
  const [text, setText] = useState("");
  const [targetLangs, setTargetLangs] = useState([]);
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(false);
  const [detectedLang, setDetectedLang] = useState("");

  const getLanguageNameFromFranc = (francCode) => {
    const lang = iso6393.find((l) => l.iso6393 === francCode);
    const iso1 = lang?.iso6391;
    const match = languages.find((l) => l.code === iso1);
    return match ? { name: match.name, code: match.code } : { name: lang?.name || "Unknown", code: "" };
  };

  const handleTextChange = (e) => {
    const input = e.target.value;
    setText(input);

    const code = franc(input);
    if (code !== "und") {
      const langObj = getLanguageNameFromFranc(code);
      setDetectedLang(langObj.code);
    } else {
      setDetectedLang("");
    }
  };

  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    if (e.target.checked) {
      if (targetLangs.length < 5) {
        setTargetLangs([...targetLangs, value]);
      } else {
        alert("You can select up to 5 languages only.");
        e.target.checked = false;
      }
    } else {
      setTargetLangs(targetLangs.filter((lang) => lang !== value));
    }
  };

  const handleTranslate = async () => {
    if (!text || targetLangs.length === 0) return;
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/translate", {
        text,
        target_langs: targetLangs,
        source_lang: detectedLang || "en"
      });
      setTranslations(response.data.translations || {});
    } catch (error) {
      alert("Translation failed");
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied!");
  };

  const handleSpeak = (text, langCode) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = langCode;
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="container">
      <h1>üåç Multi-Language Translator</h1>
      <textarea
        placeholder="Enter text..."
        value={text}
        onChange={handleTextChange}
        rows="4"
      />
      {detectedLang && (
        <div style={{ fontWeight: "bold", color: "#2e3a59", margin: "10px 0" }}>
          üîç Detected Language: {languages.find(l => l.code === detectedLang)?.name || "Unknown"}
        </div>
      )}
      <div className="checkbox-list">
        {languages.map((lang) => (
          <label key={lang.code}>
            <input
              type="checkbox"
              value={lang.code}
              onChange={handleCheckboxChange}
              checked={targetLangs.includes(lang.code)}
            />
            {languageIcons[lang.code]} {lang.name}
          </label>
        ))}
      </div>

      <button onClick={handleTranslate} disabled={loading}>
        {loading ? "Translating..." : "Translate"}
      </button>

      <div className="results">
        {Object.entries(translations).map(([lang, result]) => (
          <div className="card" key={lang}>
            <h3>{languageIcons[lang]} {languages.find(l => l.code === lang)?.name}</h3>
            <p>{result}</p>
            <div className="card-buttons">
              <button onClick={() => handleCopy(result)}>üìã Copy</button>
              <button onClick={() => handleSpeak(result, lang)}>üîä Read</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
